drop table if exists sys_operate_log;CREATE TABLE `sys_operate_log` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',  `module` varchar(32) DEFAULT NULL COMMENT '系统模块',  `user_id` int(11) DEFAULT NULL COMMENT '操作人用户ID',  `create_time` timestamp NULL DEFAULT NULL COMMENT '操作时间',  `operate_desc` varchar(100) DEFAULT NULL COMMENT '操作描述',  `exception_msg` varchar(1000) DEFAULT NULL COMMENT '异常信息描述',  `operate_status` bigint(4) DEFAULT NULL COMMENT '操作状态1、成功;2、失败',  `request_method` varchar(100) DEFAULT NULL COMMENT '请求方法',  `request_params` varchar(1000) DEFAULT NULL COMMENT '请求参数',  `request_host` varchar(20) DEFAULT NULL COMMENT '主机',  `request_class` varchar(100) DEFAULT NULL COMMENT '类名',  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8