mapper.select:
SELECT ga.id,ga.startid,ga.typeid,ga.contract,ga.isbond,ga.pname,ga.pnumber,ga.price,ga.premium,ga.time,ga.num,ga.par, ga.pdescribe,ga.status,ga.delivergoodstatus,ga.collectgoodstatus,ga.endtime,ga.name,gp.imageurl,gat.type AS atype FROM gc_joinactivityrecord gj LEFT JOIN gc_activity ga ON ga.id=gj.aid LEFT JOIN gc_activitytype gat ON ga.typeid=gat.id LEFT JOIN gc_pimage gp ON gp.aid=ga.id where 1=1 AND gj.uid=#{uid}
AND ga.status=2
AND ga.getid=#{uid}
AND ga.status IN (3,4,5)
group by ga.id order by gj.time desc limit #{pageNo},#{pageSize}

SELECT ga.id,ga.startid,ga.typeid,ga.contract,ga.isbond,ga.pname,ga.pnumber,ga.price,ga.premium,ga.time,ga.num,ga.par, ga.pdescribe,ga.status,ga.delivergoodstatus,ga.collectgoodstatus,ga.endtime,ga.name,gp.imageurl,gat.type AS atype FROM gc_activity ga LEFT JOIN gc_activitytype gat ON ga.typeid=gat.id LEFT JOIN gc_pimage gp ON gp.aid=ga.id where ga.startid=#{uid}
AND ga.status=2 GROUP BY ga.id ORDER BY ga.endtime DESC
AND ga.status IN(0,1) GROUP BY ga.id ORDER BY ga.time DESC
AND ga.status=3 AND getid IS NOT NULL GROUP BY ga.id ORDER BY ga.endtime DESC
limit #{pageNo},#{pageSize}
